mapper.select:
select * from anafor_user
where user_id = #{user_id}
and user_pw = #{user_pw}

mapper.update:
update anafor_user
set user_pw=#{user_pw}, user_name=#{user_name}, user_tel=#{user_tel}, user_birth=#{user_birth}, user_gender=#{user_gender} 
where user_id =#{user_id}

update anafor_user
set user_pw=#{user_pw}
where user_id =#{user_id}
